import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';

import Header from './components/layout/Header';
import Home from './components/pages/Home';

import './App.css';
import axios from 'axios';
import PokemonList from './components/pokemonlist/PokemonList';
import TypeList from './components/typelist/TypeList';
import PokemonDetail from './components/pokemondetail/PokemonDetail';

const App = (props) => {
  const [state, setState] = useState({
    currentId: '',
    name: '',
    height: '',
    weight: '',
    base_experience: '',
    pokemonTypes: [],
    pokemonAbilities: [],
  });

  const [pokemons, setPokemons] = useState([]);
  const [types, setTypes] = useState([]);

  useEffect(() => {
    axios
      .get('https://pokeapi.co/api/v2/pokemon')
      .then((res) => setPokemons(res.data.results));
    axios
      .get('https://pokeapi.co/api/v2/type')
      .then((res) => setState({ ...state, types: res.data.results }));
  });

  const getDetails = (id) => {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`).then((res) =>
      setState({
        ...state,
        currentId: res.data.id.toString(),
        name: res.data.name,
        height: res.data.height.toString(),
        weight: res.data.weight.toString(),
        base_experience: res.data.base_experience.toString(),
        pokemonTypes: res.data.types.map((item) => {
          return item.type.name;
        }),
        pokemonAbilities: res.data.abilities.map((item) => {
          return item.ability.name;
        }),
      })
    );
  };

  let content = (
    <Router>
      <div className='App'>
        <Header />
        <Route
          exact
          path='/'
          render={() => (
            <React.Fragment>
              <Home />
            </React.Fragment>
          )}
        />
        <Route
          path='/pokemons'
          render={(props) => (
            <React.Fragment>
              <PokemonList pokemons={state.pokemons} getDetails={getDetails} />
            </React.Fragment>
          )}
        />
        <Route
          path='/types'
          render={(props) => (
            <React.Fragment>
              <TypeList types={state.types} />
            </React.Fragment>
          )}
        />
        <Route
          path={'/pokemon/:pokemonId'}
          render={(props) => (
            <React.Fragment>
              <PokemonDetail
                currentId={state.currentId}
                name={state.name}
                height={state.height}
                weight={state.weight}
                base_experience={state.base_experience}
                pokemonTypes={state.pokemonTypes}
                pokemonAbilities={state.pokemonAbilities}
              />
            </React.Fragment>
          )}
        />
      </div>
    </Router>
  );

  return content;
};

export default App;
