import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';

import Header from './components/layout/Header';

import './App.css';
import axios from 'axios';
import PokemonList from './components/pokemonlist/PokemonList';
import TypeList from './components/typelist/TypeList';
import PokemonDetail from './components/pokemondetail/PokemonDetail';

class App extends Component {
  state = {
    pokemons: [],
    types: [],
    detail: [],
  };

  componentDidMount() {
    axios
      .get('https://pokeapi.co/api/v2/pokemon')
      .then((res) => this.setState({ pokemons: res.data.results }));
    axios
      .get('https://pokeapi.co/api/v2/type')
      .then((res) => this.setState({ types: res.data.results }));
  }

  renderPokemon = (routerProps) => {
    let pokemonId = parseInt(routerProps.match.params.pokemonId);
    axios
      .get(`https://pokeapi.co/api/v2/pokemon/${pokemonId}`)
      .then((res) => this.setState({ detail: res.data }));
    return;
  };

  render() {
    console.log(this.state);
    return (
      <Router>
        <div className='App'>
          <Header />
          <Route
            exact
            path='/'
            render={() => (
              <React.Fragment>
                <div>Main page</div>
              </React.Fragment>
            )}
          />
          <Route
            path='/pokemons'
            render={(props) => (
              <React.Fragment>
                <div>Pok√©mons page</div>
                <PokemonList pokemons={this.state.pokemons} />
              </React.Fragment>
            )}
          />
          <Route
            path='/types'
            render={(props) => (
              <React.Fragment>
                <div>Types page</div>
                <TypeList types={this.state.types} />
              </React.Fragment>
            )}
          />
          <Route
            path={'/pokemon/:pokemonId'}
            render={
              this.renderPokemon
              // <React.Fragment>

              //   <div>Pokemon Detail Page</div>
              //   <PokemonDetail />
              // </React.Fragment>
            }
          />
        </div>
      </Router>
    );
  }
}

export default App;
